INSERT INTO `template` VALUES (1,1,'Allotment Letter','ONE WEST-Allotment Letter','<div class=\"WordSection1\">\n<p>&nbsp;</p>\n\n<h1 style=\"margin-left:68px; margin-right:68px; text-align:center\"><span style=\"font-size:14pt\"><span style=\"font-family:Georgia,serif\"><u><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">ALLOTMENT&nbsp; LETTER</span></u></span></span></h1>\n\n<p style=\"margin-left:9px; margin-right:352px\">&nbsp;</p>\n\n<p><span style=\"font-size:14pt\"><span style=\"font-family:Georgia,serif\">To </span></span></p>\n\n<p><span style=\"font-size:14pt\"><span style=\"font-family:Georgia,serif\">Mr/Mrs.<strong> {{Payment.Customer}}</strong></span></span></p>\n\n<p>&nbsp;</p>\n\n<div><strong><span style=\"font-size:16px\">&nbsp; SUBJECT : ALLOTMENT OF UNIT IN ASOPLAV &quot;ONE WEST&quot;</span></strong></div>\n\n<p>&nbsp;</p>\n\n<div style=\"margin-left:9px\"><span style=\"font-size:11pt\"><span style=\"font-family:Georgia,serif\"><strong><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">RERA REGI. No</span></span></u></strong><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">. :&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;PR/GJ/VADODARA/VADODARA/Others/RAA03823/251018 .</span></span></strong></span></span></div>\n\n<p style=\"margin-left:9px\">&nbsp;</p>\n\n<p style=\"margin-left:9px\"><span style=\"font-size:14pt\"><span style=\"font-family:Georgia,serif\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Dear sir,</span></span></span></p>\n\n<p>&nbsp;</p>\n\n<p style=\"margin-left:9px; margin-right:9px; text-align:justify\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:18.6667px\">You have been alloted Office/Shop No. </span></span><strong><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:18.6667px\">{{Payment.Unit.No}}</span></span></strong><span style=\"font-fami